import React, { useState, useEffect } from 'react'
import { supabase } from '../utils/supabase'
import { Plus, ArrowUp, ArrowDown, Clock } from 'lucide-react'

const SplitFriends = () => {
  const [friends, setFriends] = useState([])
  const [showAddForm, setShowAddForm] = useState(false)
  const [selectedFriend, setSelectedFriend] = useState(null)
  const [transactions, setTransactions] = useState([])
  const [formData, setFormData] = useState({
    friendName: '',
    friendNumber: '',
    amount: '',
    type: 'credit',
    note: ''
  })
  const [editingTransaction, setEditingTransaction] = useState(null)

  useEffect(() => {
    fetchFriends()
  }, [])

  useEffect(() => {
    if (selectedFriend) {
      fetchTransactions(selectedFriend.id)
    }
  }, [selectedFriend])

  const fetchFriends = async () => {
    try {
      const { data, error } = await supabase
        .from('friends')
        .select('*')
        .order('created_at', { ascending: false })

      if (error) throw error
      setFriends(data || [])
    } catch (error) {
      console.error('Error fetching friends:', error)
    }
  }

  const fetchTransactions = async (friendId) => {
    try {
      const { data, error } = await supabase
        .from('transactions')
        .select('*')
        .eq('friend_id', friendId)
        .order('created_at', { ascending: false })

      if (error) throw error
      setTransactions(data || [])
    } catch (error) {
      console.error('Error fetching transactions:', error)
    }
  }

  const handleAddFriend = async (e) => {
    e.preventDefault()
    try {
      const { data: { user } } = await supabase.auth.getUser()

      const { error } = await supabase
        .from('friends')
        .insert([{
          user_id: user.id,
          friend_name: formData.friendName,
          friend_number: formData.friendNumber,
          balance: 0
        }])

      if (error) throw error

      setFormData({ friendName: '', friendNumber: '', amount: '', type: 'credit', note: '' })
      setShowAddForm(false)
      fetchFriends()
    } catch (error) {
      console.error('Error adding friend:', error)
      alert(`Error: ${error.message}`)
    }
  }

  const handleTransaction = async (e) => {
    e.preventDefault()
    try {
      const { data: { user } } = await supabase.auth.getUser()
      const amount = parseFloat(formData.amount)

      // Calculate new balance
      const balanceChange = formData.type === 'credit' ? amount : -amount
      const newBalance = selectedFriend.balance + balanceChange

      if (editingTransaction) {
        return handleUpdateTransaction(e)
      }

      // Add transaction with balance at time
      const { error: transError } = await supabase
        .from('transactions')
        .insert([{
          user_id: user.id,
          friend_id: selectedFriend.id,
          amount: amount,
          type: formData.type,
          note: formData.note,
          balance_at_time: newBalance,
          status: 'active'
        }])

      if (transError) throw transError

      // Update friend balance
      const { error: updateError } = await supabase
        .from('friends')
        .update({ balance: newBalance })
        .eq('id', selectedFriend.id)

      if (updateError) throw updateError

      setFormData({ ...formData, amount: '', note: '' })
      setEditingTransaction(null)
      fetchFriends()
      fetchTransactions(selectedFriend.id)

      // Update selected friend balance
      setSelectedFriend({ ...selectedFriend, balance: newBalance })
    } catch (error) {
      console.error('Error adding transaction:', error)
      alert(`Error: ${error.message}`)
    }
  }

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString()
  }

  const handleEditTransaction = (transaction) => {
    setEditingTransaction(transaction)
    setFormData({
      ...formData,
      amount: transaction.amount.toString(),
      type: transaction.type,
      note: transaction.note || ''
    })
  }

  const handleUpdateTransaction = async (e) => {
    e.preventDefault()
    try {
      const amount = parseFloat(formData.amount)
      const oldAmount = parseFloat(editingTransaction.amount)
      const oldType = editingTransaction.type

      // Calculate balance adjustment
      const oldChange = oldType === 'credit' ? oldAmount : -oldAmount
      const newChange = formData.type === 'credit' ? amount : -amount
      const balanceAdjustment = newChange - oldChange
      const newBalance = selectedFriend.balance + balanceAdjustment

      // Update transaction with edited status
      const { error: updateError } = await supabase
        .from('transactions')
        .update({
          amount: amount,
          type: formData.type,
          note: formData.note,
          status: 'edited',
          balance_at_time: newBalance
        })
        .eq('id', editingTransaction.id)

      if (updateError) throw updateError

      // Update friend balance
      const { error: friendError } = await supabase
        .from('friends')
        .update({ balance: newBalance })
        .eq('id', selectedFriend.id)

      if (friendError) throw friendError

      setFormData({ ...formData, amount: '', note: '' })
      setEditingTransaction(null)
      fetchFriends()
      fetchTransactions(selectedFriend.id)
      setSelectedFriend({ ...selectedFriend, balance: newBalance })
    } catch (error) {
      console.error('Error updating transaction:', error)
      alert(`Error: ${error.message}`)
    }
  }

  const handleDeleteTransaction = async (transaction) => {
    if (window.confirm('Are you sure you want to delete this transaction?')) {
      try {
        // Calculate balance adjustment
        const oldChange = transaction.type === 'credit' ? parseFloat(transaction.amount) : -parseFloat(transaction.amount)
        const newBalance = selectedFriend.balance - oldChange

        // Mark transaction as deleted
        const { error: updateError } = await supabase
          .from('transactions')
          .update({ 
            status: 'deleted',
            balance_at_time: newBalance
          })
          .eq('id', transaction.id)

        if (updateError) throw updateError

        // Update friend balance
        const { error: friendError } = await supabase
          .from('friends')
          .update({ balance: newBalance })
          .eq('id', selectedFriend.id)

        if (friendError) throw friendError

        fetchFriends()
        fetchTransactions(selectedFriend.id)
        setSelectedFriend({ ...selectedFriend, balance: newBalance })
      } catch (error) {
        console.error('Error deleting transaction:', error)
        alert(`Error: ${error.message}`)
      }
    }
  }

  return (
    <div className="split-friends">
      <div className="friends-header">
        <h1>Split Friends</h1>
        <button 
          className="add-btn"
          onClick={() => setShowAddForm(true)}
        >
          <Plus size={18} />
          Add Friend
        </button>
      </div>

      {showAddForm && (
        <div className="friend-form">
          <h3>Add New Friend</h3>
          <form onSubmit={handleAddFriend}>
            <input
              type="text"
              placeholder="Friend Name"
              value={formData.friendName}
              onChange={(e) => setFormData({...formData, friendName: e.target.value})}
              required
            />
            <input
              type="text"
              placeholder="Unique Number"
              value={formData.friendNumber}
              onChange={(e) => setFormData({...formData, friendNumber: e.target.value})}
              required
            />
            <div className="form-actions">
              <button type="submit">Add Friend</button>
              <button type="button" onClick={() => setShowAddForm(false)}>Cancel</button>
            </div>
          </form>
        </div>
      )}

      <div className="friends-list">
        {friends.map((friend) => (
          <div 
            key={friend.id} 
            className={`friend-card ${selectedFriend?.id === friend.id ? 'selected' : ''}`}
            onClick={() => setSelectedFriend(friend)}
          >
            <div className="friend-info">
              <h4>{friend.friend_name}</h4>
              <p>#{friend.friend_number}</p>
            </div>
            <div className={`friend-balance ${friend.balance >= 0 ? 'positive' : 'negative'}`}>
              ₹{Math.abs(friend.balance).toFixed(2)}
              {friend.balance >= 0 ? ' (To take)' : ' (To give)'}
            </div>
          </div>
        ))}
      </div>

      {selectedFriend && (
        <div className="transaction-section">
          <h3>Transactions with {selectedFriend.friend_name}</h3>

          <div className="transaction-form">
            <form onSubmit={handleTransaction}>
              <div className="transaction-type">
                <label>
                  <input
                    type="radio"
                    value="debit"
                    checked={formData.type === 'debit'}
                    onChange={(e) => setFormData({...formData, type: e.target.value})}
                  />
                  Taken (Friend paid for you)
                </label>
                <label>
                  <input
                    type="radio"
                    value="credit"
                    checked={formData.type === 'credit'}
                    onChange={(e) => setFormData({...formData, type: e.target.value})}
                  />
                  Given (You paid for friend)
                </label>
              </div>
              <input
                type="number"
                step="0.01"
                placeholder="Amount (₹)"
                value={formData.amount}
                onChange={(e) => setFormData({...formData, amount: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Note (optional)"
                value={formData.note}
                onChange={(e) => setFormData({...formData, note: e.target.value})}
              />
              <button type="submit">Add Transaction</button>
            </form>
          </div>

          <div className="transactions-list">
            {transactions.map((transaction) => (
              <div key={transaction.id} className="transaction-row">
                {transaction.type === 'debit' ? (
                  <>
                    <div className="transaction-item taken-item">
                      <div className="transaction-amount">
                        <ArrowDown className="taken-icon" />
                        ₹{transaction.amount.toFixed(2)}
                      </div>
                      <div className="transaction-details">
                        <p className="transaction-note">{transaction.note || 'No note'}</p>
                        <p className="transaction-time">
                          <Clock size={14} />
                          {formatDate(transaction.created_at)}
                        </p>
                      </div>
                      <div className="transaction-total">
                        ₹{Math.abs(transaction.balance_at_time || 0).toFixed(2)} {(transaction.balance_at_time || 0) >= 0 ? '(To take)' : '(To give)'}
                      </div>
                    </div>
                    <div className="transaction-spacer"></div>
                    <div className="transaction-empty"></div>
                  </>
                ) : (
                  <>
                    <div className="transaction-empty"></div>
                    <div className="transaction-spacer"></div>
                    <div className="transaction-item given-item">
                      <div className="transaction-amount">
                        <ArrowUp className="given-icon" />
                        ₹{transaction.amount.toFixed(2)}
                      </div>
                      <div className="transaction-details">
                        <p className="transaction-note">{transaction.note || 'No note'}</p>
                        <p className="transaction-time">
                          <Clock size={14} />
                          {formatDate(transaction.created_at)}
                        </p>
                      </div>
                      <div className="transaction-total">
                        ₹{Math.abs(transaction.balance_at_time || 0).toFixed(2)} {(transaction.balance_at_time || 0) >= 0 ? '(To take)' : '(To give)'}
                      </div>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

export default SplitFriends
